sysauto_script.script:
//Get today's time and Date
	var rightNow = new GlideDateTime();
	var tomorrow = gs.daysAgo(-1);
	gs.info(rightNow);
	gs.info(tomorrow);

	//Query database for NeedIt Tak records with Due date between rightNow and tomorrow. 
	//Only return NeedIt Tasks that do not have a state value of Closed Complete (3).
	var dueSoonTasks = new GlideRecord('x_58872_needit_needit_task');
	dueSoonTasks.addQuery('state','!=',3);
	dueSoonTasks.addQuery('due_date','<=',tomorrow);
	dueSoonTasks.addQuery('due_date','>=',rightNow);

	dueSoonTasks.query();
	//Write a log message for each Task record
	while(dueSoonTasks.next()){
		gs.info("Due soon NeedIt Task record = " + dueSoonTasks.number);
		gs.eventQueue('x_58872_needit.duesoonNeedItTask', dueSoonTasks, dueSoonTasks.number, gs.getUserName());
	}
